00615     sqlite3_finalize(pStmt);
00616 
00617     return 0;
00618 }
00619 
00620 int SQLiteHelper::updateChangedInFile(sqlite3* db, int file_id, AttributeToCheck attr_to_change) {
00621     char* zErrMsg = 0;
00622     int rc;
00623     sqlite3_stmt* pStmt;
00624     int retVal = 0;
00625     char* current_time = getCurrentTime();
00626     char* sql_stmt;
00627     
00628     switch (attr_to_change)
00629     {
00630     case 1:
00631         rc = sqlite3_prepare_v3(db, sql_set_inchanges_hashedval, -1, 0, &pStmt, NULL);
00632         break;
00633     case 2:
00634         rc = sqlite3_prepare_v3(db, sql_set_inchanges_filesize, -1, 0, &pStmt, NULL);
00635         break;
00636     case 3:
00637         rc = sqlite3_prepare_v3(db, sql_set_inchanges_lastmodified, -1, 0, &pStmt, NULL);
00638         break;
00639     default:
00640         return -1;
00641         cout << "Unknown attribute. Nothing saved." << endl;
00642         break;
00643     }
00644 
00645     if (rc == SQLITE_OK) {
00646         sqlite3_bind_int(pStmt, 1, file_id);
00647     }
00648     else {
00649         fprintf(stderr, "updateChangedInFiles:Failed to execute statement: %s\n", sqlite3_errmsg(db));
00650         sqlite3_finalize(pStmt);
00651         return -1;
00652     }
00653 
00654     rc = sqlite3_step(pStmt);
00655 
00656     if (rc == SQLITE_BUSY) {
00657         cout << "\nDatabase is busy" << endl;
00658         while (rc == SQLITE_BUSY)
00659         {
00660             rc = sqlite3_step(pStmt);
00661         }
00662     }
00663     else if (rc == SQLITE_ERROR) {
00664         printf("\nIn updateChangedInFiles execution failed: %s\n", sqlite3_errmsg(db));
00665     }
00666     else if (rc == SQLITE_MISUSE) {
00667         cout << "\nA MISUSE has been deteced in updateChangedInFiles." << endl;
00668     }
00669     else if (rc == SQLITE_DONE) {
00670         cout << "\nsqlite3_step executed successfully." << endl;
00671     }
00672     else {
00673         cout << "\nNot sure what happened in updateChangedInFiles." << endl;
00674     }
00675 
00676     sqlite3_finalize(pStmt);
00677 
00678     return 0;
00679 }
